import React, { useState } from "react";
import { Form } from "react-bootstrap";

import Card from "../UI/Card";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import classes from "./AddUser.module.css";

const AddUser = (props) => {
  const [error, setError] = useState();
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredCoin, setEnteredCoin] = useState("BTC");
  const [enteredValue, setEnteredValue] = useState("");

  const addUserHandler = (event) => {
    event.preventDefault();
    if (enteredAmount.trim().length === 0 || enteredValue.trim().length === 0) {
      setError({
        title: "Invalid input",
        message: "Please enter a valid amount and value (non-empty values).",
      });
      return;
    }
    props.onAddUser(enteredCoin, enteredAmount, enteredValue);
    setEnteredCoin("BTC");
    setEnteredAmount("");
    setEnteredValue("");
  };

  const coinChangeHandler = (event) => {
    setEnteredCoin(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const valueChangeHandler = (event) => {
    setEnteredValue(event.target.value);
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <div>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="coin">Coin</label>
          <Form.Select
            id="coin"
            type="number"
            value={enteredCoin}
            onChange={coinChangeHandler}
          >
            <option value="BTC">Bitcoin</option>
            <option value="ETH">Ether</option>
            <option value="ADA">Cardano</option>
            <option value="BSC">Binance Smart Chain</option>
          </Form.Select>
          <label htmlFor="amount">Amount of {enteredCoin}</label>
          <input
            id="amount"
            type="number"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
          <label htmlFor="value">Value in USD</label>
          <input
            id="value"
            type="number"
            value={enteredValue}
            onChange={valueChangeHandler}
          />
          <Button type="submit">Add Purchase</Button>
        </form>
      </Card>
    </div>
  );
};

export default AddUser;
